// ---------------------------------------------------------------------------------------
// Variables
// ---------------------------------------------------------------------------------------

// Using var
// 'var' is function-scoped and can be re-assigned.
var variable1: string = "This is a var variable";

// Using let
// 'let' is block-scoped and can be re-assigned.
// Block-scoped means the variable is only accessible within the block (e.g., inside a function, loop, or conditional) where it is defined.
let variable2: string = "This is a let variable";

// Using const
// 'const' is block-scoped and cannot be re-assigned.
const variable3: string = "This is a const variable";
